export declare const EXTENSION_STYLES = "\n  /* Enhanced title row styling with badge */\n  yt-formatted-string[id=\"video-title\"] {\n    display: flex;\n    align-items: center;\n    line-height: 1.4;\n  }\n\n  /* YouTube chips container centering */\n  #chips-content, ytd-feed-filter-chip-bar-renderer #chips-content {\n    display: flex;\n    align-items: center;\n    justify-content: center;\n    padding-bottom: 8px;\n  }\n\n  /* Filter Chips Container */\n  .ytosc-filter-container {\n    display: flex;\n    gap: 12px;\n    align-items: center;\n    margin-left: auto;\n    padding-left: 16px;\n  }\n\n  /* Individual Filter Chip */\n  .ytosc-filter-chip {\n    display: inline-flex;\n    align-items: center;\n    border-radius: 10px;\n    overflow: hidden;\n    transition: all 0.2s ease;\n    cursor: pointer;\n  }\n\n  /* Filter Chip Button */\n  .ytosc-filter-chip-button {\n    display: flex;\n    align-items: center;\n    padding: 10px 20px;\n    font-size: 14px;\n    font-weight: 500;\n    border: none;\n    background-color: #065fd4;\n    color: #ffffff;\n    cursor: pointer;\n    border-radius: 10px;\n    transition: all 0.2s ease;\n    white-space: nowrap;\n    font-family: 'Roboto', 'Arial', sans-serif;\n  }\n\n  .ytosc-filter-chip-button:hover {\n    background-color: #0d7df7;\n  }\n\n  /* Active Filter Chip */\n  .ytosc-filter-chip--active .ytosc-filter-chip-button {\n    background-color: #1a73e8;\n    box-shadow: 0 2px 8px rgba(26, 115, 232, 0.3);\n  }\n\n  .ytosc-filter-chip--active:hover .ytosc-filter-chip-button {\n    background-color: #1557b0;\n  }\n\n  /* Clear Filter Chip (Red) */\n  .ytosc-filter-chip--clear .ytosc-filter-chip-button {\n    background-color: #dc2626;\n    color: #ffffff;\n  }\n\n  .ytosc-filter-chip--clear:hover .ytosc-filter-chip-button {\n    background-color: #ef4444;\n  }\n\n  .ytosc-filter-chip--clear.ytosc-filter-chip--active .ytosc-filter-chip-button {\n    background-color: #b91c1c;\n    box-shadow: 0 2px 8px rgba(185, 28, 28, 0.3);\n  }\n\n  .ytosc-filter-chip--clear.ytosc-filter-chip--active:hover .ytosc-filter-chip-button {\n    background-color: #991b1b;\n  }\n\n  /* Badge positioning within titles */\n  .ytosc-badge {\n    display: inline-flex;\n    align-items: center;\n    justify-content: center;\n    padding: 3px 8px;\n    margin-right: 6px;\n    border-radius: 6px;\n    font-size: 11px;\n    font-weight: 700;\n    line-height: 1;\n    white-space: nowrap;\n    letter-spacing: 0.5px;\n    transition: all 0.2s ease;\n    cursor: pointer;\n    vertical-align: middle;\n  }\n\n  .ytosc-badge:hover {\n    transform: translateY(-1px);\n    filter: brightness(1.1);\n  }\n  \n  .ytosc-badge--gold {\n    background: linear-gradient(135deg, #FF6B6B, #F44336);\n    color: #FFFFFF;\n  }\n\n  .ytosc-badge--silver {\n    background: linear-gradient(135deg, #FFD93D, #FF9800);\n    color: #000000;\n  }\n\n  .ytosc-badge--bronze {\n    background: linear-gradient(135deg, #FFF176, #FFEB3B);\n    color: #000000;\n  }\n\n  .ytosc-badge--default {\n    background: linear-gradient(135deg, #BDBDBD, #9E9E9E);\n    color: #FFFFFF;\n  }\n  \n  /* Modal Overlay */\n  .ytosc-modal-overlay {\n    position: fixed;\n    top: 0;\n    left: 0;\n    right: 0;\n    bottom: 0;\n    background: rgba(0, 0, 0, 0.85);\n    backdrop-filter: blur(4px);\n    z-index: 999999;\n    display: flex;\n    align-items: center;\n    justify-content: center;\n    padding: 20px;\n    animation: ytosc-fadeIn 0.2s ease;\n  }\n  \n  @keyframes ytosc-fadeIn {\n    from { opacity: 0; }\n    to { opacity: 1; }\n  }\n  \n  @keyframes ytosc-slideUp {\n    from { \n      opacity: 0;\n      transform: translateY(20px);\n    }\n    to { \n      opacity: 1;\n      transform: translateY(0);\n    }\n  }\n  \n  /* Modal Container */\n  .ytosc-modal {\n    background: #ffffff;\n    border-radius: 16px;\n    max-width: 600px;\n    width: 100%;\n    max-height: 90vh;\n    overflow-y: auto;\n    box-shadow: 0 20px 60px rgba(0, 0, 0, 0.3);\n    animation: ytosc-slideUp 0.3s ease;\n    position: relative;\n  }\n  \n  /* Modal Header */\n  .ytosc-modal-header {\n    position: relative;\n    padding: 0;\n    border-bottom: 1px solid #e5e7eb;\n  }\n  \n  .ytosc-modal-thumbnail {\n    width: 100%;\n    aspect-ratio: 16/9;\n    object-fit: cover;\n    border-radius: 16px 16px 0 0;\n    background: #000;\n  }\n  \n  .ytosc-modal-close {\n    position: absolute;\n    top: 12px;\n    right: 12px;\n    width: 36px;\n    height: 36px;\n    border-radius: 50%;\n    background: rgba(0, 0, 0, 0.7);\n    backdrop-filter: blur(8px);\n    border: none;\n    color: white;\n    font-size: 24px;\n    cursor: pointer;\n    display: flex;\n    align-items: center;\n    justify-content: center;\n    transition: all 0.2s ease;\n    z-index: 10;\n  }\n  \n  .ytosc-modal-close:hover {\n    background: rgba(0, 0, 0, 0.9);\n    transform: scale(1.1);\n  }\n  \n  /* Modal Body */\n  .ytosc-modal-body {\n    padding: 24px;\n  }\n  \n  .ytosc-modal-title {\n    margin: 0 0 20px 0;\n    font-size: 18px;\n    font-weight: 600;\n    color: #111827;\n    line-height: 1.4;\n  }\n  \n  /* Score Display */\n  .ytosc-modal-score {\n    display: flex;\n    align-items: center;\n    gap: 16px;\n    padding: 20px;\n    background: linear-gradient(135deg, #f9fafb 0%, #f3f4f6 100%);\n    border-radius: 12px;\n    margin-bottom: 24px;\n  }\n  \n  .ytosc-modal-score-badge {\n    font-size: 32px;\n    font-weight: 800;\n    padding: 12px 20px;\n    border-radius: 8px;\n    box-shadow: 0 2px 8px rgba(0, 0, 0, 0.1);\n  }\n  \n  .ytosc-modal-score-info {\n    flex: 1;\n  }\n  \n  .ytosc-modal-score-label {\n    font-size: 12px;\n    color: #6b7280;\n    text-transform: uppercase;\n    letter-spacing: 0.5px;\n    margin-bottom: 4px;\n  }\n  \n  .ytosc-modal-score-value {\n    font-size: 16px;\n    font-weight: 600;\n    color: #111827;\n  }\n  \n  /* Stats Grid */\n  .ytosc-modal-stats {\n    display: grid;\n    grid-template-columns: repeat(2, 1fr);\n    gap: 16px;\n    margin-bottom: 24px;\n  }\n  \n  .ytosc-modal-stat {\n    padding: 16px;\n    background: #f9fafb;\n    border-radius: 8px;\n    border: 1px solid #e5e7eb;\n  }\n  \n  .ytosc-modal-stat-label {\n    font-size: 12px;\n    color: #6b7280;\n    margin-bottom: 6px;\n    display: block;\n  }\n  \n  .ytosc-modal-stat-value {\n    font-size: 20px;\n    font-weight: 700;\n    color: #111827;\n    display: block;\n  }\n  \n  /* Analysis Section */\n  .ytosc-modal-analysis {\n    padding: 20px;\n    background: #eff6ff;\n    border-radius: 8px;\n    border-left: 4px solid #3b82f6;\n  }\n  \n  .ytosc-modal-analysis-title {\n    font-size: 14px;\n    font-weight: 600;\n    color: #1e40af;\n    margin: 0 0 12px 0;\n  }\n  \n  .ytosc-modal-analysis-text {\n    font-size: 14px;\n    color: #1e40af;\n    line-height: 1.6;\n    margin: 0;\n  }\n  \n  /* Performance Bar */\n  .ytosc-performance-bar {\n    margin-top: 16px;\n    padding-top: 16px;\n    border-top: 1px solid #e5e7eb;\n  }\n  \n  .ytosc-performance-label {\n    font-size: 12px;\n    color: #6b7280;\n    margin-bottom: 8px;\n  }\n  \n  .ytosc-performance-track {\n    height: 8px;\n    background: #e5e7eb;\n    border-radius: 4px;\n    overflow: hidden;\n    position: relative;\n  }\n  \n  .ytosc-performance-fill {\n    height: 100%;\n    border-radius: 4px;\n    transition: width 0.5s ease;\n  }\n";
